import React, {Component} from "react";
import { StyleSheet, Text, View, TextInput } from 'react-native';
import ChatBody from "./src/ChatBody";
import ChatFooter from "./src/ChatFooter";
import io from "socket.io-client";

const socket = io('http://localhost:4000', { transports: ['websocket', 'polling', 'flashsocket'] });
class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      chatMessage: ""
    }
  }

  /*
  state = {
    chatMessage: ""
  }
  */

  submitChatMessage = (childData) => {
    console.log(childData);
    this.setState({chatMessage: 'hello'});
    console.log(this.state.chatMessage);
    socket.emit("message", this.state.chatMessage);
    this.setState({chatMessage: ""});
  }

  render(){
    return(
      <View style={styles.chat}>
        <View style={styles.chat__main}>
          <ChatBody />
          <ChatFooter parentCallback = {this.submitChatMessage}/>
        </View>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  chat: {
    width: "100%",
    height: "100vh",
    display: "flex",
    alignItems: "center"
  },
  chat__main: {
    height: "100%",
    flex: "0.8"
  }
});

export default App;
