import React from "react";
import { StyleSheet, Text, View, TextInput, Button, Header } from 'react-native';

const ChatBody = () => {
    return(
        <View>
        <Header style={styles.chat__mainHeader}>
            <Text>Chat</Text>
            <Button style={styles.leaveChat__btn} onPress={handleLeaveChat}>Leave Chat</Button>
        </Header>
        <View style={styles.message__container}>
            <View style={styles.message__chats}>
                <Text style={styles.sender__name}>You</Text>
                <View style={styles.message__sender}>
                    <Text>Hello there!</Text>
                </View>
            </View>

            <View style={styles.message__chats}>
                <Text>Other</Text>
                <View style={styles.message__recipient}>
                    <Text>Hey, I'm good</Text>
                </View>
            </View>

            <View style={styles.message__status}>
                <Text>Someone is typing...</Text>
            </View>
        </View>
        </View>
    )
}

const styles = StyleSheet.create({
    chat__mainHeader: {
        width: "100%",
        height: "10vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "20px",
        backgroundColor: "#f9f5eb"
    },
    leaveChat__btn: {
        padding: "10px",
        width: "150px",
        border: "none",
        outline: "none",
        backgroundColor: "#d1512d",
        cursor: "pointer",
        color: "#eae3d2"
    },
    message__container: {
        width: "100%",
        height: "80vh",
        backgroundColor: "#fff",
        padding: "20px",
    },
    message__chats: {
        fontSize: "13px"
    },
    sender__name: {
        textAlign: "right"
      },
    message__sender: {
        maxWidth: "300px",
        padding: "10px",
        borderRadius: "10px",
        marginLeft: "auto",
        fontSize: "15px"
    },
    message__recipient: {
        backgroundColor: "#f5ccc2",
        width: "300px",
        padding: "10px",
        borderRadius: "10px",
        fontSize: "15px"
    },
    message__status: {
        position: "fixed",
        bottom: "50px",
        fontSize: "13px",
        fontStyle: "italic"
    }
  });

export default ChatBody;
