import React, {Component} from 'react';
import {View, TextInput, Text} from "react-native";

class ChatFooter extends Component {
    constructor(props){
        super(props);
        this.sendChatMessage.bind(this);
        this.state = {
            msg: ""
        }
    }

    sendChatMessage(){
        this.props.parentCallback(this.state.msg);
    }

    render(){
        return (
            <View>
                <TextInput value = {this.state.chatMessage}
                onChangeText = {(text) => {
                    this.setState({msg: text})
                }}
                onSubmitEditing = {() => this.sendChatMessage()}></TextInput>
            </View>
          );
    }
};

export default ChatFooter;
