import React, {Component, useEffect} from "react";
import { StyleSheet, Text, View, TextInput, Button } from 'react-native';
import ChatBody from "./src/ChatBody";
import ChatFooter from "./src/ChatFooter";
import io from "socket.io-client";

const socket = io('http://localhost:4000', { transports: ['websocket', 'polling', 'flashsocket'] });
class App extends Component {
  constructor(props){
    super(props);
    this.submitChatMessage.bind(this);
    this.state = {
      chatMessage: "",
      messages: ""
    }
  }

  /*
  submitChatMessage = (childData) => {
    console.log(childData);
    this.setState({chatMessage: "hello"});
    console.log(this.state.chatMessage);
    socket.emit("message", this.state.chatMessage);
    this.setState({chatMessage: ""});
  }
  */

  componentDidMount() {
    socket.on("message", data => this.setState({messages: data}));
    console.log(this.state.messages);
  }

  submitChatMessage() {
    socket.emit("message", this.state.chatMessage);
    this.setState({chatMessage: ""});
  }

  render(){
    return(
      <View style={styles.chat}>
        <View style={styles.chat__main}>
          <ChatBody/>
          <View style={styles.chat__footer}>
          <TextInput style={styles.form} value = {this.state.chatMessage}
                placeholder = "Write a message..."
                onChangeText = {(text) => {
                    this.setState({chatMessage: text})
                }}
                onSubmitEditing = {() => this.submitChatMessage()}>
          </TextInput>
          <Button style={styles.sendBtn}>SEND</Button>
          </View>
        </View>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  chat__footer: {
    width: "100%",
      minHeight: 100,
        backgroundColor: "white",
        paddingVertical: 30,
        paddingHorizontal: 15,
        justifyContent: "center",
        flexDirection: "row",
  },
  form: {
    borderWidth: 1,
        padding: 15,
        flex: 1,
        marginRight: 50,
        borderRadius: 20,
  },
  sendBtn: {
    width: "50%",
        backgroundColor: "green",
        borderRadius: 3,
        alignItems: "center",
        justifyContent: "center",
        borderRadius: 50,
  }
});

export default App;
